mapper.select:
SELECT * FROM ( SELECT P.PROD_NO AS prodNo , P.PROD_TITLE AS prodTitle , P.PROD_PRICE AS prodPrice , P.PROD_IMG AS prodImage , P.DISCOUNT_RATE AS discountRate , P.CATEGORY_NO AS categoryNo , P.CATEGORY_NM AS categoryNm , P.PROD_HITS AS prodHits FROM PRODUCT P
ORDER BY P.PROD_HITS DESC
) WHERE ROWNUM < 4;
