mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, b.* from ( SELECT PROD_NO AS prodNo , PROD_TITLE AS prodTitle , PROD_PRICE AS prodPrice , PROD_IMG AS prodImage , DISCOUNT_RATE AS discountRate , PROD_SELLER AS prodSeller , PROD_HITS AS prodHits , REG_DATE AS regDate , (SELECT COUNT(*) FROM REVIEW WHERE PROD_NO = P.PROD_NO) AS reviewCnt , (SELECT SUM(REW_GRADE) FROM REVIEW WHERE PROD_NO = P.PROD_NO) AS starGrade FROM PRODUCT P WHERE CATEGORY_NM = #{codeVal}
AND CATEGORY_NO = #{codeSeqNo}
AND PROD_PRICE >= #{minPrice:VARCHAR}
AND PROD_PRICE <= #{maxPrice:VARCHAR}
ORDER BY PROD_HITS DESC
ORDER BY PROD_PRICE DESC
ORDER BY PROD_PRICE ASC
ORDER BY REG_DATE DESC
)b )a WHERE a.RNUM>=#{startNo} AND a.RNUM<=#{endNo}

		SELECT COUNT(*)
		FROM PRODUCT
		WHERE CATEGORY_NM = #{codeVal}

    	   AND CATEGORY_NO = #{codeSeqNo}
